                $collIds[] = $valueIds['ids']['id'];
            }
            $options['conditions'] = array(
                $cndmake,
                'users.delete_status' => 'N',
                'users.verification_status' => 'T',
                'users.block' => 'N',
                'club_purchase_account.randm_key !=' => '',
                'club_purchase_account.id =' => $collIds

            );


        }
        $options['joins'] = array(
            array(
                'table' => 'users',
