                    'User.id' => $usersIds, 'User.role_id !=' => array('1', '2', '3', '10'), 'User.delete_status' => 'N',
                    "AND" => array(
                        'User.subscription_end_date <=' => date('Y-m-d', strtotime("+30 days")),
