                    ));
            }

            //print_r($criteria);exit;

        }
        $criteria = array_merge($criteria, $criteria1);

        if (!empty($this->request->data['User']['form_date'])) {
            $criteria = array_merge($criteria, array("User.subscription_start_date >= " => $this->request->data['User']['form_date']));
