    public function payment_post()
    {
        $this->layout = "ajax";
        $this->autoRendor = false;

        $this->loadModel("PaymentInstalment");
        $this->loadModel("ClubMembershipPaymentDetail");
        $this->loadModel("PaymentInstalmentDetail");
        $clubId = $this->Session->read('Auth.Club.id');
